// password

const formPassword = document.querySelector('.wpcf7__password');
const formPasswordSee = document.querySelector('.wpcf7__password-see');
const formPasswordSeeCheckbox = document.querySelector('.wpcf7__password-see-checkbox');

formPasswordSee.addEventListener('click', () => {
  if (formPasswordSeeCheckbox.checked) {
    formPassword.setAttribute('type', 'text');
  } else {
    formPassword.setAttribute('type', 'password');
  }
});