// video

const media =  document.querySelector('.row-text-image__media');
const video =  document.querySelector('.row-text-image__media-video');
const mediaPlay =  document.querySelector('.row-text-image__media-play');

mediaPlay.addEventListener('click', () => {
  media.classList.toggle('__active');
  if (mediaPlay.classList.contains('__active')) {
    // video.controls = false;
    video.pause();
  } else {
    // video.controls = true;
    video.play();
  }
  mediaPlay.classList.toggle('__active');
});




